package walmart.testdata;


public class TestDataAccessor {


	String productName;
	int quantity;
	String enterEmail;
	String enterPassword;
	

	// Getter method for product name
	public String getProductName() {
		return productName;
	}

	
// Setter method for product name 
	public void setproductName(String productName) {
		this.productName = productName;
	}
	
	

	// Getter method for quantity
	public int getquantity() {
		return quantity;
	}

	
 // Setter method for quantity
	public void setquantity(int quantity) {
		this.quantity = quantity;
	}
	
	
// Getter method for email
	public String getenterEmail() {
		return enterEmail;
	}

	
// Setter method for email
	public void setenterEmail(String enterEmail) {
		this.enterEmail = enterEmail;
	}
	
// Getter method for email Password
	public String getenterPassword() {
		return enterPassword;
	}

	
// Setter method for email Password
	public void setenterPassword(String enterPassword) {
		this.enterPassword = enterPassword;
	}	

}